import { Box, Stack, Button, Center  } from '@chakra-ui/react';
import { useState } from 'react';
import { motion } from 'framer-motion'

function App() {

const [palette, setPalette] = useState({
  one : '#81ecec',
  two : '#74b9ff',
  three : '#a29bfe',
  four : '#00cec9',
  five : '#0984e3',
  six : '#6c5ce7',
  seven : '#fab1a0',
  eight : '#ff7675',
  nine : '#fd79a8'
})

const paletteTwo = {
  one : '#cd84f1',
  two : '#ffcccc',
  three : '#ff4d4d',
  four : '#c56cf0',
  five : '#ffb8b8',
  six : '#ff3838',
  seven : '#32ff7e',
  eight : '#7efff5',
  nine : '#18dcff'
}

const paletteThree = {
  one : '#f6b93b',
  two : '#e55039',
  three : '#4a69bd',
  four : '#fa983a',
  five : '#eb2f06',
  six : '#1e3799',
  seven : '#e58e26',
  eight : '#b71540',
  nine : '#0c2461'
}

const containerVariants = {
  hidden: {
    y: '-50vh'
  },
  visible: {
    y: 0,
    transition: {
      type: 'spring',
      stiffness:120
    }
  }
}

const firstRowVariants = {
  hidden: {
    width: '10%'
  },
  visible: {
    width: '100%',
    whileHover: {
      scale: 1.1
    },
    transition: {
      delay: 0.2,
      duration: 0.5
    }
  }
}

 function handleClick (hex){
    navigator.clipboard.writeText(hex)
  }

  return (
    <motion.div variants={containerVariants} initial="hidden" animate="visible">
    <Box justifyItems='center'>
      <Stack spacing={0} direction='column'>
        <Stack spacing={0} direction='row'>
          <motion.center  w="100%"
          variants={childVariants}
          initial="hidden" 
          animate="visible"
          >
            <Button colorScheme={palette.one} bg={palette.one} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.one)}> {palette.one} </Button>
          </motion.center >
          <motion.center  w="100%"
          variants={childVariants}
          initial="hidden" 
          animate="visible"
          >
            <Button colorScheme={palette.two} bg={palette.two} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.two)}> {palette.two} </Button>
          </motion.center >
          <motion.center  w="100%"
          variants={childVariants}
          initial="hidden" 
          animate="visible"
          >
            <Button colorScheme={palette.three} bg={palette.three} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.three)}> {palette.three} </Button>
          </motion.center >
        </Stack>

        <Stack spacing={0} direction='row'>
          <Center  w="100%" bg={palette.four}>
            <Button colorScheme={palette.four} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.four)}> {palette.four} </Button>
          </Center >
          <Center  w="100%" bg={palette.five}>
            <Button colorScheme={palette.five} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(palette.five)}> {palette.five} </Button>
          </Center >
          <Center  w="100%" bg={palette.six}>
            <Button colorScheme={palette.six} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(palette.six)}> {palette.six} </Button>
          </Center > 
        </Stack>

        <Stack spacing={0} direction='row'>
          <Center  w="100%" bg={palette.seven}>
            <Button colorScheme={palette.seven} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.seven)}> {palette.seven} </Button>
          </Center >
          <Center  w="100%" bg={palette.eight}>
            <Button colorScheme={palette.eight} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(palette.eight)}> {palette.eight} </Button>
          </Center >
          <Center  w="100%" bg={palette.nine}>
            <Button colorScheme={palette.nine} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(palette.nine)}> {palette.nine} </Button>
          </Center > 
        </Stack>
      </Stack>
    </Box>
    </motion.div>
  );
}

export default App;
