import React from 'react';
import Link from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Text } from '@chakra-ui/react';

const modal = {
    visible: {opacity: 1},
    hidden: {opacity:0}
}

const Modal = ({ showModal, setShowModal, color }) => {
    return(
        <AnimatePresence exitBeforeEnter>
            {showModal && (
                <motion.div 
                    style={{width:'100vw', height:'100vh', backgroundColor: `${color}`}}
                    variants={modal}
                    initial="hidden"
                    animate="visible"
                >
                    <Text fontSize='x-large' fontWeight={900} color="white" align='center' m='center'> COPIED TO CLIPBOARD! </Text> 
                </motion.div>
            )}
        </AnimatePresence>
    )
}

export default Modal;
