import React from 'react';
import Link from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Text } from '@chakra-ui/react';

const modal = {
    visible: {
        opacity: 1, 
        height: '100vh', 
        width: '100vw', 
        margin: 'auto',
        x:0,
        transition: {
            type: 'spring',
            damping: 20,
            stiffness:240
          }
    },
    hidden: {
        opacity:0, 
        x: '100vw'
    }
}

const Modal = ({ showModal, setShowModal, color }) => {
    return(
        <AnimatePresence exitBeforeEnter>
            {showModal && (
                <motion.center
                    onClick={()=>setShowModal(!showModal)} 
                    style={{ backgroundColor: `${color}`, padding:'20%'}}
                    variants={modal}
                    initial="hidden"
                    animate="visible"
                >
                    <motion.h1 
                        style={{fontSize:86, fontWeight:900, color:'white', justifySelf:'center'}}
                        initial={{opacity:0}}
                        whileHover={{opacity:1}}
                    > COPIED TO CLIPBOARD! </motion.h1> 
                </motion.center>
            )}
        </AnimatePresence>
    )
}

export default Modal;
