import { Box, Stack, Button, Center  } from '@chakra-ui/react';
import { useState } from 'react';
import { motion } from 'framer-motion'
import Modal from './Modal';
import {useParams} from 'react-router-dom'


const DetailedPalette = (props) => {
  
  const { selected, setColor, setShowModal } = props;

    const containerVariants = {
      hidden: {
        y: '-50vh',
      },
      visible: {
        y: 0,
        transition: {
          type: 'spring',
          damping: 11,
          stiffness:120
        }
      },
    }
    
    const firstRowVariants = {
      hidden: {
        width: '0%'
      },
      visible: {
        zIndex: 3,
        width: '100%',
        transition: {
          delay: 0.2,
          duration: 0.5
        }
      },
    }
    
    const secondRowVariants = {
      hidden: {
        width: '0%'
      },
      visible: {
        width: '100%',
        transition: {
          delay: 0.3,
          duration: 0.6
        }
      }
    }
    
    const thirdRowVariants = {
      hidden: {
        width: '0%'
      },
      visible: {
        width: '100%',
        transition: {
          delay: 0.5,
          duration: 0.2
        }
      }
    }
    
     function handleClick (hex){
        navigator.clipboard.writeText(hex)
        setColor(hex)
        setShowModal(true)
      }

    return ( 
        <motion.div variants={containerVariants} initial="hidden" animate="visible">
        <Box justifyItems='center'>
          <Stack spacing={0} direction='column'>
            <Stack spacing={0} direction='row'>
              <motion.div  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={selected.one} bg={selected.one} variant="link" borderRadius={0}  color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.one)}> {selected.one} </Button>
              </motion.div>
              <motion.center  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={selected.two} bg={selected.two} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.two)}> {selected.two} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={selected.three} bg={selected.three} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.three)}> {selected.three} </Button>
              </motion.center >
            </Stack>
            
            <Stack spacing={0} direction='row'>
            <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={selected.four} bg={selected.four} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.four)}> {selected.four} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={selected.five} bg={selected.five} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.five)}> {selected.five} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={selected.six} bg={selected.six} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.six)}> {selected.six} </Button>
              </motion.center >
            </Stack>
    
            <Stack spacing={0} direction='row'>
            <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={selected.seven} bg={selected.seven} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.seven)}> {selected.seven} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={selected.eight} bg={selected.eight} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.eight)}> {selected.eight} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={selected.nine} bg={selected.nine} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(selected.nine)}> {selected.nine} </Button>
              </motion.center >
            </Stack>
          </Stack>
        </Box>
        </motion.div>
     );
}

export default DetailedPalette;
