import React from 'react';
import Link from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

const modal = {
    visible: {opacity: 1},
    hidden: {opacity:0}
}

const Modal = ({ showModal, setShowModal, color }) => {
    return(
        <AnimatePresence exitBeforeEnter>
            {showModal && (
                <motion.div 
                    style={{width:'100vw', height:'100vh', backgroundColor: `${color}`}}
                    variants={modal}
                    initial="hidden"
                    animate="visible"
                >
                    <h1 style={{fontSize:100}}>COPIED TO CLIPBOARD!</h1>
                </motion.div>
            )}
        </AnimatePresence>
    )
}

export default Modal;
