import { Box, Stack, Button, Center, Text  } from '@chakra-ui/react';
import { motion } from 'framer-motion'
import { useContext } from 'react';
import { NavBar } from './NavBar';
import { PaletteContext } from './PaletteContext';


const DetailedPalette = ({ setColor, setShowModal }) => {
  
    const containerVariants = {
      hidden: {
        y: '-50vh',
      },
      visible: {
        y: 0,
        transition: {
          type: 'spring',
          damping: 11,
          stiffness:120
        }
      },
    }
    
    const firstRowVariants = {
      hidden: {
        width: '0.1%',
      },
      visible: {
        zIndex: 3,
        width: '100%',
        transition: {
          delay: 0.2,
          duration: 0.5
        }
      },
    }
    
    const secondRowVariants = {
      hidden: {
        width: '2%',
      },
      visible: {
        width: '100%',
        transition: {
          delay: 0.3,
          duration: 0.6
        }
      }
    }
    
    const thirdRowVariants = {
      hidden: {
        width: '3%'
      },
      visible: {
        width: '100%',
        transition: {
          delay: 0.5,
          duration: 0.2
        }
      }
    }
    
     function handleClick (hex){
        navigator.clipboard.writeText(hex)
        setColor(hex)
        setShowModal(true)
      }

    const {selectedPalette} = useContext(PaletteContext)
    const palette = selectedPalette

    return (
      <>
      <NavBar /> 
        <motion.div variants={containerVariants} initial="hidden" animate="visible" marginTop={500} width='100%'>
        <Center justifyItems='center' justifySelf='center' p='5%' width='100%' >
          <Stack spacing={0} direction='column' w="100"  marginRight={0}>
            <Stack spacing={-18} direction='row' >
              <motion.center  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={palette.one} bg={palette.one} variant="link" borderRadius={0}  color="white" fontWeight={800} fontSize='x-large' w='11vw' p='10vh' onClick={()=> handleClick(palette.one)}>{palette.one}</Button>
              </motion.center>
              <motion.center  w="100%" 
              variants={firstRowVariants}
              >
                <Button colorScheme={palette.two} bg={palette.two} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='12vw' p='10vh' onClick={()=> handleClick(palette.two)}> {palette.two} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={palette.three} bg={palette.three} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='12vw' p='10vh' onClick={()=> handleClick(palette.three)}> {palette.three} </Button>
              </motion.center >
            </Stack>
            
            <Stack spacing={-16} direction='row' >
            <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={palette.four} bg={palette.four} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='80%' p='10vh' onClick={()=> handleClick(palette.four)}> {palette.four} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={palette.five} bg={palette.five} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='80%' p='10vh' onClick={()=> handleClick(palette.five)}> {palette.five} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={palette.six} bg={palette.six} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='80%' p='10vh' onClick={()=> handleClick(palette.six)}> {palette.six} </Button>
              </motion.center >
            </Stack>
    
            <Stack spacing={-16} direction='row' >
            <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={palette.seven} bg={palette.seven} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='80%' p='10vh' onClick={()=> handleClick(palette.seven)}> {palette.seven} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={palette.eight} bg={palette.eight} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='80%' p='10vh' onClick={()=> handleClick(palette.eight)}> {palette.eight} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={palette.nine} bg={palette.nine} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='80%' p='10vh' onClick={()=> handleClick(palette.nine)}> {palette.nine} </Button>
              </motion.center >
            </Stack>
          </Stack>
        </Center>
        </motion.div>
        </>
     );
}

export default DetailedPalette;
