import { Box, Stack, Button, Center  } from '@chakra-ui/react';
import { useState } from 'react';
import { motion } from 'framer-motion'
import Modal from './Modal';


const DetailedPalette = ({palette, setColor, setShowModal}) => {

  console.log('palette', palette)
    const containerVariants = {
      hidden: {
        y: '-50vh',
      },
      visible: {
        y: 0,
        transition: {
          type: 'spring',
          damping: 11,
          stiffness:120
        }
      },
    }
    
    const firstRowVariants = {
      hidden: {
        width: '0%'
      },
      visible: {
        zIndex: 3,
        width: '100%',
        transition: {
          delay: 0.2,
          duration: 0.5
        }
      },
    }
    
    const secondRowVariants = {
      hidden: {
        width: '0%'
      },
      visible: {
        width: '100%',
        transition: {
          delay: 0.3,
          duration: 0.6
        }
      }
    }
    
    const thirdRowVariants = {
      hidden: {
        width: '0%'
      },
      visible: {
        width: '100%',
        transition: {
          delay: 0.5,
          duration: 0.2
        }
      }
    }
    
     function handleClick (hex){
        navigator.clipboard.writeText(hex)
        setColor(hex)
        setShowModal(true)
      }

    return ( 
        <motion.div variants={containerVariants} initial="hidden" animate="visible">
        <Box justifyItems='center'>
          <Stack spacing={0} direction='column'>
            <Stack spacing={0} direction='row'>
              <motion.div  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={props.palette.one} bg={props.palette.one} variant="link" borderRadius={0}  color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.one)}> {props.palette.one} </Button>
              </motion.div>
              <motion.center  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={props.palette.two} bg={props.palette.two} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.two)}> {props.palette.two} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={firstRowVariants}
              >
                <Button colorScheme={props.palette.three} bg={props.palette.three} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.three)}> {props.palette.three} </Button>
              </motion.center >
            </Stack>
            
            <Stack spacing={0} direction='row'>
            <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={props.palette.four} bg={props.palette.four} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.four)}> {props.palette.four} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={props.palette.five} bg={props.palette.five} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.five)}> {props.palette.five} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={secondRowVariants}
              >
                <Button colorScheme={props.palette.six} bg={props.palette.six} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.six)}> {props.palette.six} </Button>
              </motion.center >
            </Stack>
    
            <Stack spacing={0} direction='row'>
            <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={props.palette.seven} bg={props.palette.seven} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.seven)}> {props.palette.seven} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={props.palette.eight} bg={props.palette.eight} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.eight)}> {props.palette.eight} </Button>
              </motion.center >
              <motion.center  w="100%"
              variants={thirdRowVariants}
              >
                <Button colorScheme={props.palette.nine} bg={props.palette.nine} variant="link" borderRadius={0} color="white" fontWeight={800} fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(palette.nine)}> {props.palette.nine} </Button>
              </motion.center >
            </Stack>
          </Stack>
        </Box>
        </motion.div>
     );
}

export default DetailedPalette;
