import React from 'react';
import Link from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Text } from '@chakra-ui/react';

const modal = {
    visible: {
        opacity: 1, 
        height: '100vh', 
        width: '100vw', 
        margin: 'auto',
        position: '50'},
    hidden: {
        opacity:0, 
        width:'10vw',
        height:'10vh',
        zIndex: 0}
}

const Modal = ({ showModal, setShowModal, color }) => {
    return(
        <AnimatePresence exitBeforeEnter>
            {showModal && (
                <motion.center
                    onClick={()=>setShowModal(!showModal)} 
                    style={{ backgroundColor: `${color}`}}
                    variants={modal}
                    initial="hidden"
                    animate="visible"
                >
                    <Text fontSize='x-large' fontWeight={900} color="white" align='center'> COPIED TO CLIPBOARD! </Text> 
                </motion.center>
            )}
        </AnimatePresence>
    )
}

export default Modal;
