import { Box, Stack, Button, Center  } from '@chakra-ui/react';
import { useState } from 'react';

function App() {

const [one, setOne] = useState('#81ecec')
const [two, setTwo] = useState('#74b9ff')
const [three, setThree] = useState('#a29bfe')
const [four, setFour] = useState('#00cec9')
const [five, setFive] = useState('#0984e3')
const [six, setSix] = useState('#6c5ce7')
const [seven, setSeven] = useState('#fab1a0')
const [eight, setEight] = useState('#ff7675')
const [nine, setNine] = useState('#fd79a8')


const [palette, setPalette] = useState({
  one:'#81ecec',
  two:'#74b9ff',
  three: '#a29bfe'
})

 function handleClick (arg){
    console.log('Clicked', arg)
  }

  return (
    <Box justifyItems='center'>
      <Stack spacing={0} direction='column'>
        <Stack spacing={0} direction='row'>
          <Center  w="100%" bg={one}>
            <Button colorScheme={one} variant="link" key='asd' color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(one)}> {palette.one} </Button>
          </Center >
          <Center  w="100%" bg={two}>
            <Button colorScheme={two} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(two)}> {two} </Button>
          </Center >
          <Center  w="100%" bg={three}>
            <Button colorScheme={three} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(three)}> {three} </Button>
          </Center > 
        </Stack>

        <Stack spacing={0} direction='row'>
          <Center  w="100%" bg={four}>
            <Button colorScheme={four} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(four)}> {four} </Button>
          </Center >
          <Center  w="100%" bg={five}>
            <Button colorScheme={five} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(five)}> {five} </Button>
          </Center >
          <Center  w="100%" bg={six}>
            <Button colorScheme={six} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(six)}> {six} </Button>
          </Center > 
        </Stack>

        <Stack spacing={0} direction='row'>
          <Center  w="100%" bg={seven}>
            <Button colorScheme={seven} variant="link" color="white" fontSize='x-large' w='100%' p='15vh' onClick={()=> handleClick(seven)}> {seven} </Button>
          </Center >
          <Center  w="100%" bg={eight}>
            <Button colorScheme={eight} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(eight)}> {eight} </Button>
          </Center >
          <Center  w="100%" bg={nine}>
            <Button colorScheme={nine} variant="link" color="white" fontSize='x-large'  w='100%' p='15vh' onClick={()=> handleClick(nine)}> {nine} </Button>
          </Center > 
        </Stack>
      </Stack>
    </Box>
  );
}

export default App;
